// Si selecciono "Todos", devuelvo todos
if ($('Selecciono el proyecto').first().json.Proyecto === 'Todos') {
  return $input.all()
} else {
  // Si no, filtro por el proyecto seleccionado
  return $input.all().filter(client => client.json.Proyecto.includes($('Selecciono el proyecto').first().json.Proyecto) && client.json["Whatsapp"].toString() !== '-')
}

const data = $input.first().json.data

return {
  form: [
    {
      fieldLabel: "Templates",
      fieldType: "dropdown",
      fieldOptions: data.map(e => { return {option: `${e.name}`}}),
      requiredField: true
    }
  ]
}

const body = {
  messaging_product: "whatsapp",
  recipient_type: "individual",
  to: "+5492214363975",
  type: "template",
  template: {
    name: $input.first().json.name,
    language: {
      code: $input.first().json.language
    },
    components: []
  }
}

$input.first().json.components.forEach(component => {
  if (component.type === "HEADER" && component.format !== "TEXT") {
    body.template.components.push({
      type: "header",
      parameters: [
        {
          type: component.format.toLowerCase(),
          [component.format.toLowerCase()]: {
            link: $input.first().json.components[0].example.header_handle[0],
          },
        },
      ]
    })
  }

    if (component.type === "BODY" && component.example) {
      const parameters = component.example.body_text_named_params.map(param => {
        return {
          type: "text",
          parameter_name: param.param_name,
          text: param.example
        }
      })

    body.template.components.push({
      type: "body",
      parameters
    })
  }
})

return body;

const json = $('Verifico si tiene header').first().json

const header = json.template.components.find(component => component.type === "header")

header.parameters = [{
