return json

const header = $input.first().json.template.components.filter(component => component.type === "header")

const type = header[0].parameters[0].type

return {
  type,
  link: header[0].parameters[0][type].link
}

// Obtengo el body para enviar mensaje
const json = $('Merge').first().json

// Configuro el "to"
json.to = `+${$input.first().json.Whatsapp}`

// Busco el component "body"
const body = json.template.components.find(component => component.type === "body")

// Verifico si existe el componet "body"
if (body && body.parameters.length > 0) {
  const formatedBodyParams = body.parameters.map(param => {

    const parameterName = param.parameter_name.split("_").map((word, index) => {
    return index === 0 ?
      word[0].toUpperCase() + word.slice(1).toLowerCase() :
      word.toLowerCase()
  }).join(" ")

    return {
      ...param,
      text: $input.first().json[parameterName]
    }
  })

  body.parameters = formatedBodyParams
}

return json

// const FormData = require('form-data');
// const formData = new FormData();

const allowedAudioTypes = [
  'audio/aac',
  'audio/amr',
  'audio/mpeg',
  'audio/mp4',
  'audio/ogg',
]

const allowedDocumentTypes = [
  'text/plain',
  'application/vnd.ms-excel',
  'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
  'application/msword',
  'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
  'application/vnd.ms-powerpoint',
  'application/vnd.openxmlformats-officedocument.presentationml.presentation',
  'application/pdf'
]

const allowedImageTypes = [
  'image/jpeg',
  'image/png',
]

const allowedStickerTypes = [
  'image/webp'
]

const allowedVideoTypes = [
  'video/3gpp',
  'video/mp4'
];

const allowedMimeTypes = [
  ...allowedAudioTypes,
  ...allowedDocumentTypes,
  ...allowedImageTypes,
  ...allowedStickerTypes,
  ...allowedVideoTypes
]

console.log("file size: ", $binary.data.size)

if (!allowedMimeTypes.includes($binary.data.mimeType)) {
  throw new Error(`Tipo de archivo no permitido: ${$binary.data.mimeType}`);
} else if (
  allowedAudioTypes.includes($binary.data.mimeType) && 
  $binary.data.size > 16 * 1024 * 1024
) {
  throw new Error('Los archivos de audio no pueden exceder los 16MB');
} else if (
  allowedDocumentTypes.includes($binary.data.mimeType) && 
  $binary.data.size > 100 * 1024 * 1024
) {
  throw new Error('Los documentos no pueden exceder los 100MB');
} else if (
  allowedImageTypes.includes($binary.data.mimeType) && 
  $binary.data.size > 5 * 1024 * 1024
) {
  throw new Error('Las imágenes no pueden exceder los 5MB');
} else if (
  allowedStickerTypes.includes($binary.data.mimeType) && 
  $binary.data.size > ($binary.data.animated ? 500 * 1024 : 100 * 1024)
) {
  throw new Error(`Los stickers ${$binary.data.animated ? 'animados' : 'estáticos'} no pueden exceder los ${$binary.data.animated ? '500KB' : '100KB'}`);
} else if (
  allowedVideoTypes.includes($binary.data.mimeType) && 
  $binary.data.size > 16 * 1024 * 1024
) {
  throw new Error('Los videos no pueden exceder los 16MB');
} else {
  return [{binary: $binary}];
}
