const dbMessages = $input.first().json.messages.map(message => {
    const key = Object.keys(message)[0];
    const value = message[key];

    return {
      role: key === "human" ? "user" : key,
      content: value
    };
})

const messages = [...dbMessages, {"role": "user", "content": $('Switch').item.json.messages[0].text.body }]

return {data: messages};
